mapper.select:
SELECT rent_date, rent_id, mem_id, book_id, rent_due_date, rent_finish_date, rent_status FROM rent
mem_id = #{memId}
AND rent_status = #{rentStatus}

SELECT rent_date, rent_id, mem_id, book_id, rent_due_date, rent_finish_date, rent_status FROM rent
mem_id = #{memId}
AND rent_status IN
#{status}

mapper.update:
UPDATE rent r SET (book_id, mem_id, rent_due_date, rent_finish_date, rent_status) = (SELECT book_id, mem_id, rent_due_date, rent_finish_date, rent_status FROM (
SELECT #{rent.bookId} as book_id, #{rent.memId} as mem_id, #{rent.rentDueDate} as rent_due_date, CAST(#{rent.rentFinishDate} AS DATE) as rent_finish_date, #{rent.rentStatus} as rent_status, #{rent.rentId} as rent_id FROM dual
) temp WHERE temp.rent_id = r.rent_id) WHERE r.rent_id IN
#{rent.rentId}

mapper.select:
SELECT r.rent_date, r.rent_id, r.book_id, r.rent_due_date, r.rent_finish_date, r.rent_status, b.book_title, b.book_img_path FROM rent r LEFT JOIN book b ON r.book_id = b.book_id
mem_id = #{memId}
AND rent_status = #{rentStatus}
ORDER BY r.rent_date DESC, r.rent_id ASC OFFSET #{offset} ROWS FETCH NEXT #{limit} ROWS ONLY

        SELECT COUNT(*)
        FROM rent


	            mem_id = #{memId}


	            AND rent_status = #{rentStatus}
